    <script src="{{ asset('assets/vendor/apexcharts/apexcharts.min.js') }}"></script>

    <script>
         var options = {
          series: [{
          data: [400, 430, 448]
        }],
          chart: {
          type: 'bar',
          height: 350
        },
        plotOptions: {
          bar: {
            borderRadius: 4,
            horizontal: true,
          }
        },
        dataLabels: {
          enabled: false
        },
        xaxis: {
          categories: ['Bajo Cauca', 'Norte', 'Suroeste'],
        }
        };

        var chart = new ApexCharts(document.querySelector("#chart1"), options);
        chart.render();
      



        var options2 = {
          series: [{
          data: [400, 430, 448, 470]
        }],
          chart: {
          type: 'bar',
          height: 380
        },
        plotOptions: {
          bar: {
            barHeight: '100%',
            distributed: true,
            horizontal: true,
            dataLabels: {
              position: 'bottom'
            },
          }
        },
        colors: ['#33b2df', '#546E7A', '#d4526e', '#13d8aa'
        ],
        dataLabels: {
          enabled: true,
          textAnchor: 'start',
          style: {
            colors: ['#fff']
          },
          formatter: function (val, opt) {
            return opt.w.globals.labels[opt.dataPointIndex] + ":  " + val
          },
          offsetX: 0,
          dropShadow: {
            enabled: true
          }
        },
        stroke: {
          width: 1,
          colors: ['#fff']
        },
        xaxis: {
          categories: ['Medellín', 'Bello', 'Ríonegro', 'Envigado',
          ],
        },
        yaxis: {
          labels: {
            show: false
          }
        },
        title: {
            text: '',
            align: 'center',
            floating: true
        },
        tooltip: {
          theme: 'dark',
          x: {
            show: false
          },
          y: {
            title: {
              formatter: function () {
                return ''
              }
            }
          }
        }
        };

        var chart2 = new ApexCharts(document.querySelector("#chart2"), options2);
        chart2.render();

        // Coordenadas aproximadas de Medellín
        var medellinCoords = { latMin: 6.1651, latMax: 6.3553, lngMin: -75.6673, lngMax: -75.4795 };

        // Función para generar coordenadas aleatorias dentro de un rango específico
        function getRandomCoords(latMin, latMax, lngMin, lngMax) {
            var lat = Math.random() * (latMax - latMin) + latMin;
            var lng = Math.random() * (lngMax - lngMin) + lngMin;
            return { lat: lat, lng: lng };
        }

        // Generar 100 coordenadas aleatorias dentro de Medellín
        var randomMedellinCoords = [];
        for (var i = 0; i < 20; i++) { // Reducido a 100 coordenadas
            randomMedellinCoords.push(getRandomCoords(medellinCoords.latMin, medellinCoords.latMax, medellinCoords.lngMin, medellinCoords.lngMax));
        }

        // Inicializar el mapa
        function initMap() {
            var map = new google.maps.Map(document.getElementById('map'), {
                zoom: 12,
                center: { lat: 6.2518, lng: -75.5636 } // Centro de Medellín
            });

            // Configurar la capa de calor
            var heatmap = new google.maps.visualization.HeatmapLayer({
                data: randomMedellinCoords.map(coord => new google.maps.LatLng(coord.lat, coord.lng)),
                map: map,
                radius: 50 // Ajustar el radio de difusión de los puntos de calor (en pixels)
            });
        }
    </script>
     

